import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import SimplePeer from 'simple-peer';
import ChatBox from './ChatBox';
import './VideoRoom.css';

const VideoRoom = () => {
  const socketRef = useRef();
  const peersRef = useRef([]);
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [screenStream, setScreenStream] = useState();
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [showChat, setShowChat] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);
  const userVideo = useRef();
  const screenVideo = useRef();
  const userName = useRef();

  useEffect(() => {
    // Get user media (camera/microphone)
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then(stream => {
        setStream(stream);
        userVideo.current.srcObject = stream;
      })
      .catch(error => console.error('Error accessing media devices:', error));

    // Initialize socket
    const SOCKET_SERVER = process.env.REACT_APP_SERVER_URL || 'http://localhost:5000';
    socketRef.current = io(SOCKET_SERVER);
    userName.current = `User-${Math.random().toString(36).substr(2, 9)}`;

    socketRef.current.emit('user-join', {
      userId: socketRef.current.id,
      userName: userName.current
    });

    // Handle incoming signal
    socketRef.current.on('signal', ({ from, signal }) => {
      const peerConnection = peersRef.current.find(p => p.peerID === from);
      if (peerConnection) {
        peerConnection.peer.signal(signal);
      }
    });

    // Handle user joined
    socketRef.current.on('user-joined', (data) => {
      createPeer(data.socketId, socketRef.current.id, stream);
      setUsers(prev => [...prev, data]);
    });

    // Handle current users
    socketRef.current.on('current-users', (currentUsers) => {
      currentUsers.forEach(user => {
        if (user.socketId !== socketRef.current.id) {
          createPeer(user.socketId, socketRef.current.id, stream);
        }
      });
      setUsers(currentUsers.filter(u => u.socketId !== socketRef.current.id));
    });

    // Handle user left
    socketRef.current.on('user-left', ({ socketId }) => {
      const peer = peersRef.current.find(p => p.peerID === socketId);
      if (peer) {
        peer.peer.destroy();
        peersRef.current = peersRef.current.filter(p => p.peerID !== socketId);
      }
      setPeers(prev => prev.filter(p => p.peerID !== socketId));
      setUsers(prev => prev.filter(u => u.socketId !== socketId));
    });

    // Handle chat messages
    socketRef.current.on('receive-message', (data) => {
      setMessages(prev => [...prev, { ...data, isOwn: false }]);
    });

    // Handle screen share start
    socketRef.current.on('screen-share-started', (data) => {
      console.log('User started screen sharing:', data.userName);
    });

    // Handle screen share stop
    socketRef.current.on('screen-share-stopped', (data) => {
      console.log('User stopped screen sharing:', data.userName);
    });

    return () => {
      socketRef.current.disconnect();
      stream?.getTracks().forEach(track => track.stop());
      screenStream?.getTracks().forEach(track => track.stop());
    };
  }, []);

  const createPeer = (peerSocketId, creatorId, stream) => {
    const peer = new SimplePeer({
      initiator: true,
      trickleIce: true,
      stream: stream,
      config: {
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:global.stun.twilio.com:3478' }
        ]
      }
    });

    peer.on('signal', signal => {
      socketRef.current.emit('signal', { to: peerSocketId, signal });
    });

    peer.on('stream', remoteStream => {
      const existingPeer = peersRef.current.find(p => p.peerID === peerSocketId);
      if (!existingPeer) {
        peersRef.current.push({
          peerID: peerSocketId,
          peer,
          stream: remoteStream
        });
        setPeers(prev => [...prev, { peerID: peerSocketId, stream: remoteStream }]);
      }
    });

    peer.on('error', error => {
      console.error('Peer error:', error);
    });

    return peer;
  };

  const startScreenShare = async () => {
    try {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({
        video: { cursor: 'always' },
        audio: false
      });
      setScreenStream(screenStream);
      setIsScreenSharing(true);

      socketRef.current.emit('screen-share-started', {
        userName: userName.current,
        timestamp: new Date().toISOString()
      });

      screenStream.getTracks()[0].onended = () => {
        stopScreenShare();
      };
    } catch (error) {
      console.error('Error sharing screen:', error);
      if (error.name !== 'NotAllowedError') {
        alert('Unable to share screen');
      }
    }
  };

  const stopScreenShare = () => {
    if (screenStream) {
      screenStream.getTracks().forEach(track => track.stop());
      setScreenStream(null);
      setIsScreenSharing(false);

      socketRef.current.emit('screen-share-stopped', {
        userName: userName.current
      });
    }
  };

  const toggleMute = () => {
    if (stream) {
      const audioTracks = stream.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = !track.enabled;
      });
      setIsMuted(!isMuted);
    }
  };

  const toggleVideo = () => {
    if (stream) {
      const videoTracks = stream.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = !track.enabled;
      });
      setIsVideoOff(!isVideoOff);
    }
  };

  const handleSendMessage = (message) => {
    const fullMessage = {
      ...message,
      isOwn: true
    };
    setMessages(prev => [...prev, fullMessage]);

    socketRef.current.emit('send-message', {
      text: message.text,
      userName: message.userName,
      timestamp: message.timestamp
    });
  };

  return (
    <div className="video-room-container">
      <div className="header">
        <div className="header-content">
          <h1>üé• Video Conference Meeting</h1>
          <span className="participants-badge">üë• {users.length + 1} Participants</span>
        </div>
      </div>

      <div className="main-content">
        <div className="video-section">
          {isScreenSharing && screenStream ? (
            <div className="screen-share-container">
              <div className="screen-header">
                <span>üì∫ Screen Sharing</span>
                <button className="stop-share-btn" onClick={stopScreenShare}>
                  Stop Sharing
                </button>
              </div>
              <video
                ref={screenVideo}
                autoPlay
                playsInline
                style={{ width: '100%', height: '100%', objectFit: 'contain' }}
              />
            </div>
          ) : (
            <div className="videos-grid">
              <div className="video-item local-video">
                <video
                  playsInline
                  muted
                  ref={userVideo}
                  autoPlay
                  className={isVideoOff ? 'video-off' : ''}
                />
                <div className="video-info">
                  <span className="video-name">{userName.current} (You)</span>
                  <div className="video-indicators">
                    {isMuted && <span className="indicator">üîá Muted</span>}
                    {isVideoOff && <span className="indicator">üìπ Off</span>}
                  </div>
                </div>
              </div>

              {peers.map((peer) => (
                <VideoFrame key={peer.peerID} stream={peer.stream} userName={`User-${peer.peerID.substr(0, 5)}`} />
              ))}
            </div>
          )}
        </div>

        {showChat && (
          <div className="chat-section">
            <ChatBox 
              messages={messages}
              onSendMessage={handleSendMessage}
              userName={userName.current}
            />
          </div>
        )}
      </div>

      <div className="controls-bar">
        <div className="controls-group">
          <button 
            className={`control-btn ${isMuted ? 'disabled' : ''}`}
            onClick={toggleMute}
            title={isMuted ? 'Unmute' : 'Mute'}
          >
            {isMuted ? 'üîá' : 'üéôÔ∏è'} {isMuted ? 'Unmute' : 'Mute'}
          </button>
          <button 
            className={`control-btn ${isVideoOff ? 'disabled' : ''}`}
            onClick={toggleVideo}
            title={isVideoOff ? 'Turn on camera' : 'Turn off camera'}
          >
            {isVideoOff ? 'üìπ' : 'üì∑'} {isVideoOff ? 'Camera Off' : 'Camera On'}
          </button>
          <button 
            className={`control-btn ${isScreenSharing ? 'active' : ''}`}
            onClick={isScreenSharing ? stopScreenShare : startScreenShare}
            title={isScreenSharing ? 'Stop sharing' : 'Share screen'}
          >
            üì∫ {isScreenSharing ? 'Stop Share' : 'Share Screen'}
          </button>
          <button 
            className={`control-btn ${showChat ? 'active' : ''}`}
            onClick={() => setShowChat(!showChat)}
            title="Toggle chat"
          >
            üí¨ {showChat ? 'Hide' : 'Show'} Chat
          </button>
          <button 
            className="control-btn end-call"
            onClick={() => window.location.reload()}
            title="End call"
          >
