          </button>
        </div>
      </div>
    </div>
  );
};

const VideoFrame = ({ stream, userName }) => {
  const videoRef = useRef();

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  return (
    <div className="video-item">
      <video
        playsInline
        autoPlay
        ref={videoRef}
        style={{ width: '100%', borderRadius: '8px' }}
      />
    </div>
  );
};

export default VideoRoom;
